import React, { Component } from "react";
import Coin from "./coin";

export default class coindashboard extends React.Component {
    constructor(props){
        super(props);

        this.state = {
            items: [],
            DataisLoaded: false
        }
    }
    componentDidMount() {
        this.loadBackEndData()
            .then(res=> this.setState({ 
                items: res.express,
                DataisLoaded:true
            }))
            .catch(err => console.log(err));
    }

    loadBackEndData = async() => {
        const response = await fetch('/on_start');
        const body = await response.json();

        if (response.status != 200){
            throw Error(body.message);
        }

        return body;
    }

    render(){
        let all_coins;
        for (let item in this.state.items){
            let crypto = Coin(this.state.items[item].name);
            crypto.setState(this.state.items[item]);
            all_coins += crypto.render();
        }

        return this.state.items; 

        return all_coins;
    }
}
