import React from 'react'; 
import api from './api';

const IndexPage = () => {
  let [responseData, setResponseData] = React.useState('');
  let [symbol, setSymbol] = React.useState('');
  let [message, setMessage] = React.useState('');

  const fetchData= (e) => {

    console.log("Hello");

    e.preventDefault(); 

    setMessage('Loading...');

    api.coinInfo(symbol)
    .then((response)=>{
      setResponseData(response.data);
      setMessage('');
      console.log(response);
    })
    .catch((error)=> {
      setMessage('Error');
      console.log(error);
    })
  }

  return (
    <div>
      <h1>Mining Is Doomed!</h1>
      <form onSubmit = {fetchData}>
        <fieldset>
          <legend>Ethereum Only!</legend>
            <label htmlFor="symbol"> Eth Only! 
              <input
                required
                name = "symbol"
                id = "symbol"
                type = "text"
                placeholder = "ETH Only!"
                value = {symbol}
                onChange = {(e) => setSymbol(e.target.value)}
              />
            </label>
        </fieldset>
      </form>
      <p>{message}</p>
      <h3>Symbol: {responseData ? responseData.symbol: "Sad"}</h3>
    </div>
  )
}

export default IndexPage;
